const bindAboutAccordionItem=t=>{if(!t)return!1;t.addEventListener("click",(o=>{"UL"===o.target.tagName&&[...t.querySelectorAll("li:not(:first-child)")].includes(o.target)||t.classList.toggle("about-block_open")}))},bindAboutAccordionCountriesItem=t=>{if(!t)return!1;const o=t.querySelector(".about-countries__list");t.addEventListener("click",(e=>{o.contains(e.target)||t.classList.toggle("about-countries_open")}))};window.addEventListener("load",(()=>{for(const t of document.querySelectorAll(".about-block"))bindAboutAccordionItem(t);for(const t of document.querySelectorAll(".about-countries"))bindAboutAccordionCountriesItem(t)}));