const bindNewsSlider=e=>{if(!e)return!1;return new Swiper(e,{slidesPerView:4,breakpoints:{0:{slidesPerView:2,spaceBetween:10},500:{slidesPerView:3,spaceBetween:20},1e3:{slidesPerView:4,spaceBetween:20}}})},bindNewsVerticalSlider=e=>{if(!e)return!1;return new Swiper(e,{slidesPerView:2,direction:"vertical",mousewheel:{releaseOnEdges:!0},breakpoints:{0:{slidesPerView:2},500:{slidesPerView:2},1e3:{slidesPerView:2}}})};window.addEventListener("load",(()=>{for(const e of document.querySelectorAll(".news-detail__slider_horizontal .swiper"))bindNewsSlider(e);for(const e of document.querySelectorAll(".news-detail__slider_vertical .swiper"))bindNewsVerticalSlider(e)}));